import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { AppSidebar } from "@/components/app-sidebar";
import { ThemeProvider } from "@/components/theme-provider";
import { ThemeToggle } from "@/components/theme-toggle";
import Dashboard from "@/pages/dashboard";
import VirtualMachines from "@/pages/virtual-machines";
import Networks from "@/pages/networks";
import Storage from "@/pages/storage";
import Monitoring from "@/pages/monitoring";
import Billing from "@/pages/billing";
import DiscountCoupons from "@/pages/discount-coupons";
import Resellers from "@/pages/resellers";
import WhiteLabel from "@/pages/whitelabel";
import ResellerCustomers from "@/pages/reseller-customers";
import SuperAdmin from "@/pages/super-admin";
import AdminRights from "@/pages/admin-rights";
import AllVMs from "@/pages/all-vms";
import AllKubernetes from "@/pages/all-kubernetes";
import SecuritySettings from "@/pages/security-settings";
import FeatureManagement from "@/pages/feature-management";
import PaymentGateways from "@/pages/payment-gateways";
import PricingCalculator from "@/pages/pricing-calculator";
import LoadBalancer from "@/pages/load-balancer";
import GPUInstances from "@/pages/gpu-instances";
import SSLCertificates from "@/pages/ssl-certificates";
import CDN from "@/pages/cdn";
import AutoScaling from "@/pages/auto-scaling";
import Kubernetes from "@/pages/kubernetes";
import Database from "@/pages/database";
import Marketplace from "@/pages/marketplace";
import ObjectStorage from "@/pages/object-storage";
import DNS from "@/pages/dns";
import TeamManagement from "@/pages/team-management";
import RoleManagement from "@/pages/role-management";
import UserDiscounts from "@/pages/user-discounts";
import Login from "@/pages/login";
import NotFound from "@/pages/not-found";
import Volumes from "@/pages/volumes";
import Firewall from "@/pages/firewall";
import SecurityGroups from "@/pages/security-groups";
import VPC from "@/pages/vpc";
import ElasticIP from "@/pages/elastic-ip";
import SSHKeys from "@/pages/ssh-keys";
import ImagesTemplates from "@/pages/images-templates";
import ResourceTags from "@/pages/resource-tags";
import ActivityLogs from "@/pages/activity-logs";
import { useQuery } from "@tanstack/react-query";

function Router() {
  const { data: user, isLoading } = useQuery({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!user) {
    return <Login />;
  }

  return (
    <Switch>
      <Route path="/" component={Dashboard} />
      <Route path="/vms" component={VirtualMachines} />
      <Route path="/kubernetes" component={Kubernetes} />
      <Route path="/database" component={Database} />
      <Route path="/marketplace" component={Marketplace} />
      <Route path="/object-storage" component={ObjectStorage} />
      <Route path="/dns" component={DNS} />
      <Route path="/networks" component={Networks} />
      <Route path="/storage" component={Storage} />
      <Route path="/monitoring" component={Monitoring} />
      <Route path="/billing" component={Billing} />
      <Route path="/billing/coupons" component={DiscountCoupons} />
      <Route path="/billing/user-discounts" component={UserDiscounts} />
      <Route path="/resellers" component={Resellers} />
      <Route path="/whitelabel" component={WhiteLabel} />
      <Route path="/customers" component={ResellerCustomers} />
      <Route path="/super-admin" component={SuperAdmin} />
      <Route path="/feature-management" component={FeatureManagement} />
      <Route path="/admin-rights" component={AdminRights} />
      <Route path="/all-vms" component={AllVMs} />
      <Route path="/all-kubernetes" component={AllKubernetes} />
      <Route path="/security" component={SecuritySettings} />
      <Route path="/payment-gateways" component={PaymentGateways} />
      <Route path="/pricing-calculator" component={PricingCalculator} />
      <Route path="/load-balancer" component={LoadBalancer} />
      <Route path="/gpu-instances" component={GPUInstances} />
      <Route path="/ssl-certificates" component={SSLCertificates} />
      <Route path="/cdn" component={CDN} />
      <Route path="/auto-scaling" component={AutoScaling} />
      <Route path="/team-management" component={TeamManagement} />
      <Route path="/role-management" component={RoleManagement} />
      <Route path="/volumes" component={Volumes} />
      <Route path="/firewall" component={Firewall} />
      <Route path="/security-groups" component={SecurityGroups} />
      <Route path="/vpc" component={VPC} />
      <Route path="/elastic-ip" component={ElasticIP} />
      <Route path="/ssh-keys" component={SSHKeys} />
      <Route path="/images-templates" component={ImagesTemplates} />
      <Route path="/resource-tags" component={ResourceTags} />
      <Route path="/activity-logs" component={ActivityLogs} />
      <Route component={NotFound} />
    </Switch>
  );
}

export default function App() {
  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  };

  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <TooltipProvider>
          <SidebarProvider style={style as React.CSSProperties}>
            <div className="flex h-screen w-full">
              <AppSidebar />
              <div className="flex flex-col flex-1 overflow-hidden">
                <header className="flex items-center justify-between p-4 border-b">
                  <SidebarTrigger data-testid="button-sidebar-toggle" />
                  <ThemeToggle />
                </header>
                <main className="flex-1 overflow-auto p-6">
                  <Router />
                </main>
              </div>
            </div>
          </SidebarProvider>
          <Toaster />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

