import type { Request, Response, NextFunction } from "express";
import type { IStorage } from "../storage";

export function createFeatureFlagMiddleware(storage: IStorage) {
  return function requireFeature(featureKey: string) {
    return async (req: Request, res: Response, next: NextFunction) => {
      try {
        const feature = await storage.getFeatureFlagByKey(featureKey);

        if (!feature) {
          return res.status(404).json({
            message: `Feature '${featureKey}' not found`,
          });
        }

        if (!feature.enabled) {
          return res.status(403).json({
            message: `Feature '${feature.name}' is currently disabled`,
            feature: featureKey,
          });
        }

        next();
      } catch (error: any) {
        return res.status(500).json({
          message: error.message || "Error checking feature flag",
        });
      }
    };
  };
}

client/src/pages/role-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { Shield, Plus, Edit2, Trash2, CheckCircle2, Lock } from "lucide-react";
import { Separator } from "@/components/ui/separator";

interface Role {
  id: string;
  name: string;
  description: string | null;
  isSystem: boolean;
  organizationId: string | null;
  createdAt: string;
  updatedAt: string;
}

interface Permission {
  id: string;
  key: string;
  name: string;
  description: string | null;
  category: string;
  createdAt: string;
}

export default function RoleManagement() {
  const { toast } = useToast();
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [permissionsDialogOpen, setPermissionsDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState<Role | null>(null);
  const [roleName, setRoleName] = useState("");
  const [roleDescription, setRoleDescription] = useState("");
  const [selectedPermissions, setSelectedPermissions] = useState<Set<string>>(new Set());

  // Fetch roles
  const { data: roles = [], isLoading: loadingRoles } = useQuery<Role[]>({
    queryKey: ["/api/iam/roles"],
  });

  // Fetch all permissions
  const { data: allPermissions = [] } = useQuery<Permission[]>({
    queryKey: ["/api/iam/permissions"],
  });

  // Fetch role permissions when a role is selected
  const { data: rolePermissions = [] } = useQuery<Permission[]>({
    queryKey: ["/api/iam/roles", selectedRole?.id, "permissions"],
    enabled: !!selectedRole?.id,
  });

  // Create role mutation
  const createMutation = useMutation({
    mutationFn: async (data: { name: string; description: string }) => {
      const res = await apiRequest("POST", "/api/iam/roles", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/iam/roles"] });
      setCreateDialogOpen(false);
      setRoleName("");
      setRoleDescription("");
      toast({
        title: "Role Created",
        description: "Custom role has been created successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create role",
        variant: "destructive",
      });
    },
  });

  // Update role mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: { name: string; description: string } }) => {
      const res = await apiRequest("PATCH", `/api/iam/roles/${id}`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/iam/roles"] });
      setEditDialogOpen(false);
      setSelectedRole(null);
      toast({
        title: "Role Updated",
        description: "Role has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update role",
        variant: "destructive",
      });
    },
  });

  // Delete role mutation
  const deleteMutation = useMutation({
    mutationFn: async (roleId: string) => {
      const res = await apiRequest("DELETE", `/api/iam/roles/${roleId}`);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/iam/roles"] });
      setDeleteDialogOpen(false);
      setSelectedRole(null);
      toast({
        title: "Role Deleted",
        description: "Role has been deleted successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete role",
        variant: "destructive",
      });
    },
  });

  // Assign permission mutation
  const assignPermissionMutation = useMutation({
    mutationFn: async ({ roleId, permissionId }: { roleId: string; permissionId: string }) => {
      const res = await apiRequest("POST", `/api/iam/roles/${roleId}/permissions`, {
        permissionId,
      });
      return res.json();
    },
    onSuccess: () => {
      if (selectedRole) {
        queryClient.invalidateQueries({
          queryKey: ["/api/iam/roles", selectedRole.id, "permissions"],
        });
      }
    },
  });

  // Remove permission mutation
  const removePermissionMutation = useMutation({
    mutationFn: async ({ roleId, permissionId }: { roleId: string; permissionId: string }) => {
      const res = await apiRequest("DELETE", `/api/iam/roles/${roleId}/permissions/${permissionId}`);
      return res.json();
    },
    onSuccess: () => {
      if (selectedRole) {
        queryClient.invalidateQueries({
          queryKey: ["/api/iam/roles", selectedRole.id, "permissions"],
        });
      }
    },
  });

  const handleCreateRole = () => {
    if (!roleName.trim()) {
      toast({
        title: "Error",
        description: "Role name is required",
        variant: "destructive",
      });
      return;
    }
    createMutation.mutate({ name: roleName, description: roleDescription });
  };

  const handleUpdateRole = () => {
    if (!selectedRole || !roleName.trim()) {
      toast({
        title: "Error",
        description: "Role name is required",
        variant: "destructive",
      });
      return;
    }
    updateMutation.mutate({
      id: selectedRole.id,
      data: { name: roleName, description: roleDescription },
    });
  };

  const handleOpenEditDialog = (role: Role) => {
    setSelectedRole(role);
    setRoleName(role.name);
    setRoleDescription(role.description || "");
    setEditDialogOpen(true);
  };

  const handleOpenPermissionsDialog = (role: Role) => {
    setSelectedRole(role);
    setPermissionsDialogOpen(true);
  };

  const handleSavePermissions = async () => {
    if (!selectedRole) return;

    const currentPermissionIds = new Set(rolePermissions.map((p) => p.id));
    const newPermissionIds = selectedPermissions;

    // Add new permissions
    for (const permId of Array.from(newPermissionIds)) {
      if (!currentPermissionIds.has(permId)) {
        await assignPermissionMutation.mutateAsync({
          roleId: selectedRole.id,
          permissionId: permId,
        });
      }
    }

    // Remove permissions
    for (const permId of Array.from(currentPermissionIds)) {
      if (!newPermissionIds.has(permId)) {
        await removePermissionMutation.mutateAsync({
          roleId: selectedRole.id,
          permissionId: permId,
        });
      }
    }

    setPermissionsDialogOpen(false);
    setSelectedRole(null);
    setSelectedPermissions(new Set());
    toast({
      title: "Permissions Updated",
      description: "Role permissions have been updated successfully.",
    });
  };

  // Group permissions by category
  const permissionsByCategory = allPermissions.reduce((acc, perm) => {
    if (!acc[perm.category]) {
      acc[perm.category] = [];
    }
    acc[perm.category].push(perm);
    return acc;
  }, {} as Record<string, Permission[]>);

  // Initialize selected permissions when dialog opens
  const handlePermissionsDialogOpenChange = (open: boolean) => {
    if (open) {
      const currentIds = new Set(rolePermissions.map((p) => p.id));
      setSelectedPermissions(currentIds);
    }
    setPermissionsDialogOpen(open);
  };

  if (loadingRoles) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-solid border-primary border-r-transparent"></div>
          <p className="mt-2 text-muted-foreground">Loading roles...</p>
        </div>
      </div>
    );
  }

  const systemRoles = roles.filter((r) => r.isSystem);
  const customRoles = roles.filter((r) => !r.isSystem);

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="heading-role-management">
            Role Management
          </h1>
          <p className="text-muted-foreground mt-1">
            Create custom roles and manage permissions
          </p>
        </div>
        <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-role">
              <Plus className="mr-2 h-4 w-4" />
              Create Role
            </Button>
          </DialogTrigger>
          <DialogContent data-testid="dialog-create-role">
            <DialogHeader>
              <DialogTitle>Create Custom Role</DialogTitle>
              <DialogDescription>
                Create a new role with custom permissions for your organization.
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4 py-4">
              <div className="space-y-2">
                <Label htmlFor="role-name">Role Name</Label>
                <Input
                  id="role-name"
                  placeholder="e.g., Developer, Support Agent"
                  value={roleName}
                  onChange={(e) => setRoleName(e.target.value)}
                  data-testid="input-role-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="role-description">Description</Label>
                <Textarea
                  id="role-description"
                  placeholder="Describe the purpose of this role..."
                  value={roleDescription}
                  onChange={(e) => setRoleDescription(e.target.value)}
                  rows={3}
                  data-testid="input-role-description"
                />
              </div>
            </div>
            <DialogFooter>
              <Button
                variant="outline"
                onClick={() => setCreateDialogOpen(false)}
                data-testid="button-cancel-create"
              >
                Cancel
              </Button>
              <Button
                onClick={handleCreateRole}
                disabled={createMutation.isPending}
                data-testid="button-confirm-create"
              >
                {createMutation.isPending ? "Creating..." : "Create Role"}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>

      {/* System Roles */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lock className="h-5 w-5" />
            System Roles
          </CardTitle>
          <CardDescription>
            Built-in roles that cannot be modified or deleted
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Role Name</TableHead>
                  <TableHead>Description</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {systemRoles.map((role) => (
                  <TableRow key={role.id} data-testid={`row-role-${role.id}`}>
                    <TableCell className="font-medium">
                      <div className="flex items-center gap-2">
                        <Shield className="h-4 w-4 text-primary" />
                        {role.name}
                        <Badge variant="secondary" className="ml-2">
                          System
                        </Badge>
                      </div>
                    </TableCell>
                    <TableCell className="text-muted-foreground">
                      {role.description || "No description"}
                    </TableCell>
                    <TableCell className="text-right">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleOpenPermissionsDialog(role)}
                        data-testid={`button-view-permissions-${role.id}`}
                      >
                        View Permissions
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      {/* Custom Roles */}
      <Card>
        <CardHeader>
          <CardTitle>Custom Roles</CardTitle>
          <CardDescription>
            {customRoles.length} custom role{customRoles.length !== 1 ? "s" : ""} in your organization
          </CardDescription>
        </CardHeader>
        <CardContent>
          {customRoles.length === 0 ? (
            <div className="text-center py-12">
              <Shield className="mx-auto h-12 w-12 text-muted-foreground" />
              <h3 className="mt-4 text-lg font-semibold">No custom roles yet</h3>
              <p className="text-muted-foreground mt-2">
                Create custom roles to define specific permissions for your team.
              </p>
              <Button
                className="mt-4"
                onClick={() => setCreateDialogOpen(true)}
                data-testid="button-create-first-role"
              >
                <Plus className="mr-2 h-4 w-4" />
                Create Role
              </Button>
            </div>
          ) : (
            <div className="rounded-md border">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Role Name</TableHead>
                    <TableHead>Description</TableHead>
                    <TableHead className="text-right">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {customRoles.map((role) => (
                    <TableRow key={role.id} data-testid={`row-role-${role.id}`}>
                      <TableCell className="font-medium">
                        <div className="flex items-center gap-2">
                          <Shield className="h-4 w-4" />
                          {role.name}
                        </div>
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {role.description || "No description"}
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex justify-end gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleOpenPermissionsDialog(role)}
                            data-testid={`button-manage-permissions-${role.id}`}
                          >
                            <Shield className="h-4 w-4 mr-1" />
                            Permissions
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleOpenEditDialog(role)}
                            data-testid={`button-edit-role-${role.id}`}
                          >
                            <Edit2 className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedRole(role);
                              setDeleteDialogOpen(true);
                            }}
                            data-testid={`button-delete-role-${role.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-destructive" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Role Dialog */}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent data-testid="dialog-edit-role">
          <DialogHeader>
            <DialogTitle>Edit Role</DialogTitle>
            <DialogDescription>
              Update the role name and description.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="edit-role-name">Role Name</Label>
              <Input
                id="edit-role-name"
                value={roleName}
                onChange={(e) => setRoleName(e.target.value)}
                data-testid="input-edit-role-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="edit-role-description">Description</Label>
              <Textarea
                id="edit-role-description"
                value={roleDescription}
                onChange={(e) => setRoleDescription(e.target.value)}
                rows={3}
                data-testid="input-edit-role-description"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setEditDialogOpen(false)}
              data-testid="button-cancel-edit"
            >
              Cancel
            </Button>
            <Button
              onClick={handleUpdateRole}
              disabled={updateMutation.isPending}
              data-testid="button-confirm-edit"
            >
              {updateMutation.isPending ? "Updating..." : "Update Role"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Manage Permissions Dialog */}
      <Dialog open={permissionsDialogOpen} onOpenChange={handlePermissionsDialogOpenChange}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto" data-testid="dialog-manage-permissions">
          <DialogHeader>
            <DialogTitle>
              Manage Permissions for {selectedRole?.name}
            </DialogTitle>
            <DialogDescription>
              Select which permissions this role should have.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-6 py-4">
            {Object.entries(permissionsByCategory).map(([category, permissions]) => (
              <div key={category} className="space-y-3">
                <h4 className="font-semibold text-sm">{category}</h4>
                <Separator />
                <div className="space-y-2">
                  {permissions.map((permission) => (
                    <div
                      key={permission.id}
                      className="flex items-start space-x-3 p-2 rounded-md hover-elevate"
                    >
                      <Checkbox
                        id={permission.id}
                        checked={selectedPermissions.has(permission.id)}
                        onCheckedChange={(checked) => {
                          const newSet = new Set(selectedPermissions);
                          if (checked) {
                            newSet.add(permission.id);
                          } else {
                            newSet.delete(permission.id);
                          }
                          setSelectedPermissions(newSet);
                        }}
                        disabled={selectedRole?.isSystem}
                        data-testid={`checkbox-permission-${permission.id}`}
                      />
                      <div className="flex-1">
                        <Label
                          htmlFor={permission.id}
                          className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                        >
                          {permission.name}
                        </Label>
                        {permission.description && (
                          <p className="text-xs text-muted-foreground mt-1">
                            {permission.description}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setPermissionsDialogOpen(false);
                setSelectedRole(null);
              }}
              data-testid="button-cancel-permissions"
            >
              {selectedRole?.isSystem ? "Close" : "Cancel"}
            </Button>
            {!selectedRole?.isSystem && (
              <Button
                onClick={handleSavePermissions}
                disabled={assignPermissionMutation.isPending || removePermissionMutation.isPending}
                data-testid="button-save-permissions"
              >
                {assignPermissionMutation.isPending || removePermissionMutation.isPending
                  ? "Saving..."
                  : "Save Permissions"}
              </Button>
            )}
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent data-testid="dialog-delete-role">
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Role</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete the role "{selectedRole?.name}"? This action cannot be
              undone. Users with this role will lose their associated permissions.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => selectedRole && deleteMutation.mutate(selectedRole.id)}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-delete"
            >
              {deleteMutation.isPending ? "Deleting..." : "Delete Role"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/user-discounts.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Percent, Users, TrendingDown, Save } from "lucide-react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface User {
  id: string;
  username: string;
  email: string;
  gstNumber: string;
  accountType: string;
  defaultDiscountPercentage: number;
  status: string;
}

export default function UserDiscounts() {
  const { toast } = useToast();
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [discountValue, setDiscountValue] = useState<string>("");
  const [searchTerm, setSearchTerm] = useState("");

  const { data: users = [], isLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  const updateDiscountMutation = useMutation({
    mutationFn: async ({ userId, discountPercentage }: { userId: string; discountPercentage: number }) => {
      const response = await fetch(`/api/users/${userId}/discount`, {
        method: "PATCH",
        body: JSON.stringify({ discountPercentage }),
        headers: { "Content-Type": "application/json" },
        credentials: "include",
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to update discount");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "Discount percentage updated successfully",
      });
      setSelectedUser(null);
      setDiscountValue("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update discount percentage",
        variant: "destructive",
      });
    },
  });

  const handleEditDiscount = (user: User) => {
    setSelectedUser(user);
    setDiscountValue(user.defaultDiscountPercentage?.toString() || "0");
  };

  const handleSaveDiscount = () => {
    if (!selectedUser) return;

    const discount = parseFloat(discountValue);
    if (isNaN(discount) || discount < 0 || discount > 100) {
      toast({
        title: "Invalid Value",
        description: "Discount percentage must be between 0 and 100",
        variant: "destructive",
      });
      return;
    }

    updateDiscountMutation.mutate({
      userId: selectedUser.id,
      discountPercentage: discount,
    });
  };

  const getAccountTypeColor = (type: string) => {
    switch (type) {
      case "super_admin":
        return "bg-chart-1/10 text-chart-1 border-chart-1/20";
      case "reseller":
        return "bg-chart-2/10 text-chart-2 border-chart-2/20";
      case "customer":
        return "bg-chart-3/10 text-chart-3 border-chart-3/20";
      default:
        return "bg-muted/10 text-muted-foreground border-muted/20";
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "bg-chart-2/10 text-chart-2 border-chart-2/20";
      case "suspended":
        return "bg-destructive/10 text-destructive border-destructive/20";
      case "invited":
        return "bg-chart-3/10 text-chart-3 border-chart-3/20";
      default:
        return "";
    }
  };

  // Filter users (exclude super_admins and team_members)
  const eligibleUsers = users.filter(
    (user) => user.accountType === "reseller" || user.accountType === "customer"
  );

  // Apply search filter
  const filteredUsers = eligibleUsers.filter(
    (user) =>
      user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.gstNumber.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Calculate statistics
  const totalUsers = eligibleUsers.length;
  const usersWithDiscounts = eligibleUsers.filter(
    (user) => user.defaultDiscountPercentage > 0
  ).length;
  const averageDiscount =
    eligibleUsers.length > 0
      ? eligibleUsers.reduce((sum, user) => sum + (user.defaultDiscountPercentage || 0), 0) /
        eligibleUsers.length
      : 0;

  return (
    <div className="flex flex-col gap-6">
      <div className="flex items-center justify-between gap-4 flex-wrap">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">User Discount Management</h1>
          <p className="text-muted-foreground mt-1">
            Assign permanent discount percentages to resellers and customers
          </p>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <p className="text-sm font-medium text-muted-foreground">Eligible Users</p>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-total-users">
              {totalUsers}
            </div>
            <p className="text-xs text-muted-foreground mt-1">Resellers & Customers</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <p className="text-sm font-medium text-muted-foreground">Users with Discounts</p>
            <TrendingDown className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-discounted-users">
              {usersWithDiscounts}
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              {totalUsers > 0 ? Math.round((usersWithDiscounts / totalUsers) * 100) : 0}% of total
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <p className="text-sm font-medium text-muted-foreground">Average Discount</p>
            <Percent className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-avg-discount">
              {averageDiscount.toFixed(1)}%
            </div>
            <p className="text-xs text-muted-foreground mt-1">Across all users</p>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between gap-4 flex-wrap">
            <h3 className="text-base font-medium">User Discounts</h3>
            <div className="flex-1 max-w-sm">
              <Input
                placeholder="Search by name, email, or GST..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                data-testid="input-search-users"
              />
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="text-center py-8 text-muted-foreground">Loading users...</div>
          ) : filteredUsers.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              {searchTerm ? "No users found matching your search" : "No eligible users found"}
            </div>
          ) : (
            <div className="rounded-md border">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Username</TableHead>
                    <TableHead>Email</TableHead>
                    <TableHead>GST Number</TableHead>
                    <TableHead>Account Type</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead className="text-right">Discount %</TableHead>
                    <TableHead></TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredUsers.map((user) => (
                    <TableRow key={user.id} className="hover-elevate">
                      <TableCell className="font-medium" data-testid={`user-name-${user.id}`}>
                        {user.username}
                      </TableCell>
                      <TableCell className="text-muted-foreground">{user.email}</TableCell>
                      <TableCell className="font-mono text-sm">{user.gstNumber}</TableCell>
                      <TableCell>
                        <Badge variant="outline" className={getAccountTypeColor(user.accountType)}>
                          {user.accountType}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Badge variant="outline" className={getStatusColor(user.status)}>
                          {user.status}
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right font-medium" data-testid={`discount-${user.id}`}>
                        {user.defaultDiscountPercentage || 0}%
                      </TableCell>
                      <TableCell>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleEditDiscount(user)}
                          data-testid={`button-edit-discount-${user.id}`}
                        >
                          Edit
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      <Dialog open={!!selectedUser} onOpenChange={(open) => !open && setSelectedUser(null)}>
        <DialogContent data-testid="dialog-edit-discount">
          <DialogHeader>
            <DialogTitle>Set Discount Percentage</DialogTitle>
            <DialogDescription>
              Assign a permanent discount percentage to {selectedUser?.username}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label>User Details</Label>
              <div className="rounded-md border p-3 space-y-1">
                <div className="flex items-center justify-between gap-2">
                  <span className="text-sm text-muted-foreground">Username:</span>
                  <span className="text-sm font-medium">{selectedUser?.username}</span>
                </div>
                <div className="flex items-center justify-between gap-2">
                  <span className="text-sm text-muted-foreground">Email:</span>
                  <span className="text-sm font-medium">{selectedUser?.email}</span>
                </div>
                <div className="flex items-center justify-between gap-2">
                  <span className="text-sm text-muted-foreground">Account Type:</span>
                  <Badge variant="outline" className={getAccountTypeColor(selectedUser?.accountType || "")}>
                    {selectedUser?.accountType}
                  </Badge>
                </div>
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="discount-percentage">Discount Percentage</Label>
              <div className="flex items-center gap-2">
                <Input
                  id="discount-percentage"
                  type="number"
                  min="0"
                  max="100"
                  step="0.1"
                  value={discountValue}
                  onChange={(e) => setDiscountValue(e.target.value)}
                  placeholder="Enter discount percentage"
                  data-testid="input-discount-percentage"
                />
                <span className="text-muted-foreground">%</span>
              </div>
              <p className="text-xs text-muted-foreground">
                This discount will be permanently applied to all invoices for this user
              </p>
            </div>
          </div>

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setSelectedUser(null)}
              data-testid="button-cancel-discount"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSaveDiscount}
              disabled={updateDiscountMutation.isPending}
              data-testid="button-save-discount"
            >
              <Save className="h-4 w-4 mr-2" />
              {updateDiscountMutation.isPending ? "Saving..." : "Save Discount"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/discount-coupons.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Plus, Pencil, Trash2, Tag, Calendar, Users, TrendingUp } from "lucide-react";
import type { DiscountCoupon } from "@shared/schema";

export default function DiscountCouponsPage() {
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingCoupon, setEditingCoupon] = useState<DiscountCoupon | null>(null);
  const { toast } = useToast();

  const { data: coupons = [], isLoading } = useQuery<DiscountCoupon[]>({
    queryKey: ["/api/coupons"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => apiRequest("/api/coupons", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/coupons"] });
      setIsCreateOpen(false);
      toast({ title: "Coupon created successfully!" });
    },
    onError: (error: any) => {
      toast({ title: "Failed to create coupon", description: error.message, variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) =>
      apiRequest(`/api/coupons/${id}`, "PATCH", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/coupons"] });
      setEditingCoupon(null);
      toast({ title: "Coupon updated successfully!" });
    },
    onError: (error: any) => {
      toast({ title: "Failed to update coupon", description: error.message, variant: "destructive" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => apiRequest(`/api/coupons/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/coupons"] });
      toast({ title: "Coupon deleted successfully!" });
    },
    onError: (error: any) => {
      toast({ title: "Failed to delete coupon", description: error.message, variant: "destructive" });
    },
  });

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center min-h-96">
          <div className="text-center">
            <div className="text-lg">Loading discount coupons...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="heading-coupons">Discount Coupons</h1>
          <p className="text-muted-foreground">Create and manage discount coupons for your customers</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-coupon">
              <Plus className="w-4 h-4 mr-2" />
              Create Coupon
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create Discount Coupon</DialogTitle>
              <DialogDescription>Create a new discount coupon for your customers</DialogDescription>
            </DialogHeader>
            <CouponForm
              onSubmit={(data) => createMutation.mutate(data)}
              isPending={createMutation.isPending}
            />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Coupons</CardTitle>
            <Tag className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-total-coupons">{coupons.length}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Coupons</CardTitle>
            <TrendingUp className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-active-coupons">
              {coupons.filter((c) => c.isActive).length}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Uses</CardTitle>
            <Users className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-total-uses">
              {coupons.reduce((sum, c) => sum + c.timesUsed, 0)}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Expiring Soon</CardTitle>
            <Calendar className="w-4 h-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="stat-expiring-soon">
              {coupons.filter((c) => {
                if (!c.validUntil) return false;
                const daysUntilExpiry = Math.ceil(
                  (new Date(c.validUntil).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24)
                );
                return daysUntilExpiry >= 0 && daysUntilExpiry <= 7;
              }).length}
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>All Coupons</CardTitle>
          <CardDescription>Manage your discount coupons</CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Code</TableHead>
                <TableHead>Name</TableHead>
                <TableHead>Discount</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Uses</TableHead>
                <TableHead>Valid Until</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {coupons.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={8} className="text-center py-8 text-muted-foreground">
                    No coupons created yet. Create your first coupon to get started.
                  </TableCell>
                </TableRow>
              ) : (
                coupons.map((coupon) => (
                  <TableRow key={coupon.id} data-testid={`row-coupon-${coupon.id}`}>
                    <TableCell>
                      <code className="px-2 py-1 bg-muted rounded text-sm font-mono" data-testid={`text-code-${coupon.id}`}>
                        {coupon.code}
                      </code>
                    </TableCell>
                    <TableCell data-testid={`text-name-${coupon.id}`}>{coupon.name}</TableCell>
                    <TableCell data-testid={`text-discount-${coupon.id}`}>
                      {coupon.discountType === "percentage"
                        ? `${coupon.discountValue}%`
                        : `â‚¹${coupon.discountValue}`}
                    </TableCell>
                    <TableCell>
                      <Badge variant="outline" data-testid={`badge-duration-${coupon.id}`}>
                        {coupon.durationType === "once"
                          ? "One-time"
                          : coupon.durationType === "forever"
                          ? "Forever"
                          : `${coupon.durationMonths}mo`}
                      </Badge>
                    </TableCell>
                    <TableCell data-testid={`text-uses-${coupon.id}`}>
                      {coupon.timesUsed}
                      {coupon.maxUses ? ` / ${coupon.maxUses}` : " / âˆž"}
                    </TableCell>
                    <TableCell data-testid={`text-validity-${coupon.id}`}>
                      {coupon.validUntil
                        ? new Date(coupon.validUntil).toLocaleDateString()
                        : "No expiry"}
                    </TableCell>
                    <TableCell>
                      <Badge
                        variant={coupon.isActive ? "default" : "secondary"}
                        data-testid={`badge-status-${coupon.id}`}
                      >
                        {coupon.isActive ? "Active" : "Inactive"}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Dialog open={editingCoupon?.id === coupon.id} onOpenChange={(open) => !open && setEditingCoupon(null)}>
                          <DialogTrigger asChild>
                            <Button
                              size="icon"
                              variant="ghost"
                              onClick={() => setEditingCoupon(coupon)}
                              data-testid={`button-edit-${coupon.id}`}
                            >
                              <Pencil className="w-4 h-4" />
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-2xl">
                            <DialogHeader>
                              <DialogTitle>Edit Coupon</DialogTitle>
                              <DialogDescription>Update coupon details</DialogDescription>
                            </DialogHeader>
                            <CouponForm
                              initialData={coupon}
                              onSubmit={(data) => updateMutation.mutate({ id: coupon.id, data })}
                              isPending={updateMutation.isPending}
                            />
                          </DialogContent>
                        </Dialog>
                        <Button
                          size="icon"
                          variant="ghost"
                          onClick={() => {
                            if (confirm(`Delete coupon "${coupon.code}"?`)) {
                              deleteMutation.mutate(coupon.id);
                            }
                          }}
                          data-testid={`button-delete-${coupon.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

function CouponForm({
  initialData,
  onSubmit,
  isPending,
}: {
  initialData?: DiscountCoupon;
  onSubmit: (data: any) => void;
  isPending: boolean;
}) {
  const [code, setCode] = useState(initialData?.code || "");
  const [name, setName] = useState(initialData?.name || "");
  const [description, setDescription] = useState(initialData?.description || "");
  const [discountType, setDiscountType] = useState<"percentage" | "fixed">(
    (initialData?.discountType as "percentage" | "fixed") || "percentage"
  );
  const [discountValue, setDiscountValue] = useState(initialData?.discountValue?.toString() || "");
  const [durationType, setDurationType] = useState<"once" | "forever" | "repeating">(
    (initialData?.durationType as "once" | "forever" | "repeating") || "once"
  );
  const [durationMonths, setDurationMonths] = useState(initialData?.durationMonths?.toString() || "");
  const [maxUses, setMaxUses] = useState(initialData?.maxUses?.toString() || "");
  const [minOrderAmount, setMinOrderAmount] = useState(initialData?.minOrderAmount?.toString() || "");
  const [validUntil, setValidUntil] = useState(
    initialData?.validUntil ? new Date(initialData.validUntil).toISOString().split("T")[0] : ""
  );
  const [isActive, setIsActive] = useState(initialData?.isActive !== false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const data: any = {
      code: code.toUpperCase(),
      name,
      description: description || undefined,
      discountType,
      discountValue: parseInt(discountValue),
      durationType,
      durationMonths: durationType === "repeating" ? parseInt(durationMonths) : undefined,
      maxUses: maxUses ? parseInt(maxUses) : undefined,
      minOrderAmount: minOrderAmount ? parseInt(minOrderAmount) : undefined,
      validUntil: validUntil ? new Date(validUntil) : undefined,
      isActive,
    };

    onSubmit(data);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid gap-4 md:grid-cols-2">
        <div className="space-y-2">
          <Label htmlFor="code">Coupon Code *</Label>
          <Input
            id="code"
            data-testid="input-code"
            placeholder="SAVE20"
            value={code}
            onChange={(e) => setCode(e.target.value.toUpperCase())}
            required
          />
        </div>
        <div className="space-y-2">
          <Label htmlFor="name">Display Name *</Label>
          <Input
            id="name"
            data-testid="input-name"
            placeholder="20% Off"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
      </div>

      <div className="space-y-2">
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          data-testid="input-description"
          placeholder="Optional description for internal use"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <div className="space-y-2">
          <Label htmlFor="discountType">Discount Type *</Label>
          <Select value={discountType} onValueChange={(v) => setDiscountType(v as "percentage" | "fixed")}>
            <SelectTrigger data-testid="select-discount-type">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="percentage">Percentage (%)</SelectItem>
              <SelectItem value="fixed">Fixed Amount (â‚¹)</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <div className="space-y-2">
          <Label htmlFor="discountValue">
            {discountType === "percentage" ? "Percentage (1-100)" : "Amount in â‚¹"} *
          </Label>
          <Input
            id="discountValue"
            data-testid="input-discount-value"
            type="number"
            min="1"
            max={discountType === "percentage" ? "100" : undefined}
            value={discountValue}
            onChange={(e) => setDiscountValue(e.target.value)}
            required
          />
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <div className="space-y-2">
          <Label htmlFor="durationType">Duration Type *</Label>
          <Select value={durationType} onValueChange={(v) => setDurationType(v as "once" | "forever" | "repeating")}>
            <SelectTrigger data-testid="select-duration-type">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="once">One-time</SelectItem>
              <SelectItem value="forever">Forever</SelectItem>
              <SelectItem value="repeating">Repeating (Monthly)</SelectItem>
            </SelectContent>
          </Select>
        </div>
        {durationType === "repeating" && (
          <div className="space-y-2">
            <Label htmlFor="durationMonths">Number of Months *</Label>
            <Input
              id="durationMonths"
              data-testid="input-duration-months"
              type="number"
              min="1"
              value={durationMonths}
              onChange={(e) => setDurationMonths(e.target.value)}
              required
            />
          </div>
        )}
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <div className="space-y-2">
          <Label htmlFor="maxUses">Max Uses (optional)</Label>
          <Input
            id="maxUses"
            data-testid="input-max-uses"
            type="number"
            min="1"
            placeholder="Unlimited"
            value={maxUses}
            onChange={(e) => setMaxUses(e.target.value)}
          />
        </div>
        <div className="space-y-2">
          <Label htmlFor="minOrderAmount">Min Order Amount in â‚¹ (optional)</Label>
          <Input
            id="minOrderAmount"
            data-testid="input-min-order"
            type="number"
            min="0"
            placeholder="No minimum"
            value={minOrderAmount}
            onChange={(e) => setMinOrderAmount(e.target.value)}
          />
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <div className="space-y-2">
          <Label htmlFor="validUntil">Valid Until (optional)</Label>
          <Input
            id="validUntil"
            data-testid="input-valid-until"
            type="date"
            value={validUntil}
            onChange={(e) => setValidUntil(e.target.value)}
          />
        </div>
        <div className="flex items-center space-x-2 pt-8">
          <Switch
            id="isActive"
            data-testid="switch-active"
            checked={isActive}
            onCheckedChange={setIsActive}
          />
          <Label htmlFor="isActive">Active</Label>
        </div>
      </div>

      <div className="flex justify-end gap-2 pt-4">
        <Button type="submit" disabled={isPending} data-testid="button-submit-coupon">
          {isPending ? "Saving..." : initialData ? "Update Coupon" : "Create Coupon"}
        </Button>
      </div>
    </form>
  );
}

